from tkinter import *from PIL import ImageTk, Imagefrom tkinter import filedialogimport osroot = Tk()root.minsize(650, 650)root.maxsize(650, 650)open_img = ImageTk.PhotoImage(Image.open("open.png"))save_img = ImageTk.PhotoImage(Image.open("save.png"))exit_img = ImageTk.PhotoImage(Image.open("exit.jpg"))label_file_name = Label(root, text="File Name")label_file_name.place(relx=0.28, rely=0.03, anchor=CENTER)input_file_name = Entry(root)input_file_name.place(relx=0.46, rely=0.03, anchor=CENTER)mytext = Text(root, height=35, width=80)mytext.place(relx=0.5, rely=0.55, anchor=CENTER)name = ""def openfile():    global name    mytext.delete(1.0,END)    input_file_name.delete(0,END)    textfile = filedialog.askopenfilename(title = "Open Text File", filetypes = (("Text Files", "*.txt")))    print(textfile)    name = os.path.basename(textfile)    formattedname = name.split('.')[0]    input_file_name.insert(END, formattedname)    root.title(formattedname)    textfile = open(name, 'r')    paragraph = textfile.read()    mytext.insert(END,paragraph)    textfile.close()open_btn = Button(root, image = open_img, text="Open File", command=openfile)open_btn.place(relx=0.05, rely=0.03, anchor=CENTER)root.mainloop()